import React, { Component } from 'react';

import * as apiCalls from '../api/apiCalls';
import Spinner from './Spinner';
import WaveView from './WaveView';

class WaveFeed extends Component {

    state = {
        page:{
            content: []
        },
        isLoadingWaves: false,
        newWaveCount: 0,
    }

    componentDidMount(){
        this.setState({isLoadingWaves: true})
        apiCalls.loadWaves(this.props.user)
            .then(response => {
                this.setState({page: response.data, isLoadingWaves: false},
                    this.checkCount);
            });
    }

    checkCount = () => {
        const waves = this.state.page.content;
        if (waves.length === 0){
            return;
        }
        const topWave = waves[0];
        apiCalls.loadNewWaveCount(topWave.id, this.props.user)
            .then(response => {
                this.setState({newWaveCount: response.data.count})
            });
    }

    onClickLoadMore = () => {
        const waves = this.state.page.content;
        if (waves.length === 0){
            return;
        }
        const waveAtBottom = waves[waves.length - 1];
        apiCalls.loadOldWaves(waveAtBottom.id, this.props.user)
            .then(response => {
                const page = {...this.state.page};
                page.content = [...page.content, ...response.data.content];
                page.last = response.data.last;
                this.setState({page});
            })
    }

    render() {
        if (this.state.isLoadingWaves){
            return (<Spinner/>);
        }
        if (this.state.page.content.length === 0){
            return (
                <div className = "card card-header text-center">
                    There are no waves
                </div>
            );
        } 
        return <div>
            {this.state.newWaveCount > 0 && (
                <div className = "card card-header text-center">
                    There is 1 new wave
                </div>
            )}
            {this.state.page.content.map((wave) => {
                return <WaveView key = {wave.id} wave = {wave}/>
            })}
            {this.state.page.last === false && (
                <div 
                    className = "card card-header text-center"
                    onClick = {this.onClickLoadMore}
                    style = {{cursor: 'pointer'}}
                >
                    Load More
                </div>
            )}
        </div>
    }
}

export default WaveFeed;
