import React from 'react';

import ProfileImage from './ProfileImage';
import Input from './Input';


const ProfileCard = (props) => {
    const { username, displayName, image } = props.user;

    const showEditButton = props.isEditable && !props.inEditMode;

    return (
        <div className = 'card'>
            <div className = 'card-header text-center'>
                <ProfileImage 
                    image = {image}
                    width = {"200"} 
                    height = {"200"} 
                    class = {"rounded-circle shadow"}
                />
            </div>
            <div className = 'card-body text-center'>
                {!props.inEditMode && (
                    <h4>
                        {`${displayName}@${username}`}
                    </h4>
                )}
                {showEditButton && (
                    <button className = "btn btn-outline-success" onClick = {props.onClickEdit}>
                        Edit
                    </button>
                )}
                {props.inEditMode && (
                    <div className = "mb-2">
                        <Input
                            value = {displayName}
                            label={`Change Display Name for ${username}`}
                            onChange = {props.onChangeDisplayName}
                        />
                        <input
                            type = "file"
                        />
                    </div>
                )}
                {
                    props.inEditMode && (
                    <div>
                        <button className = "btn btn-primary" onClick = {props.onClickSave}>
                            Save
                        </button>
                        <button className = "btn btn-secondary ml-1" onClick = {props.onClickCancel}>
                            Cancel
                        </button>
                    </div>

                        
                    )
                }
            </div>
        </div>
    );
};

export default ProfileCard;
