import React, { Component } from 'react';
import { connect } from 'react-redux';

import ProfileImage from './ProfileImage';
import * as apiCalls from '../api/apiCalls';

class WaveSubmit extends Component {

    state = {
        focused: false,
        content: undefined,
    }

    onFocus = () => {
        this.setState({focused: true});
    }

    onClickExtinguishWave = () => {
        this.setState({
            focused: false,
            content: '',
        });
    }

    onChangeContent = (event) => {
        const value = event.target.value;
        this.setState({content: value});
    }

    onClickRunWave = () => {
        const body = {
            content: this.state.content,
        };
        apiCalls.postWave(body)
            .then((response) => {
                this.setState({
                    focused: false,
                    content: ''
                });
            });
    }

    render() {
        return (
            <div className = "card d-flex flex-row p-1">
                <ProfileImage
                    className = "rounded-circle m-1"
                    width = "32"
                    height = "32"
                    image = {this.props.loggedInUser.image}
                />
                <div className = "flex-fill">
                    <textarea 
                        className = "form-control w-100" 
                        rows = {this.state.focused ? 3 : 1}
                        onFocus = {this.onFocus}
                        value = {this.state.content}
                        onChange = {this.onChangeContent}
                    /> 
                    {this.state.focused && (
                        <div className = "text-right mt-1">
                            <button 
                                className = "btn btn-success"
                                onClick = {this.onClickRunWave}    
                            >
                                Run wave
                            </button>  
                            <button className = "btn btn-light ml-1" onClick = {this.onClickExtinguishWave}>
                                Extinguish wave
                            </button>
                        </div>  
                    )}
                    
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        loggedInUser: state
    }
}

export default connect(mapStateToProps)(WaveSubmit);
