import React, { Component } from 'react';

import * as apiCalls from '../api/apiCalls';
import Spinner from './Spinner';

class WaveFeed extends Component {

    state = {
        page:{
            content: []
        },
        isLoadingWaves: false,
    }

    componentDidMount(){
        this.setState({isLoadingWaves: true})
        apiCalls.loadWaves(this.props.user)
            .then(response => {
                this.setState({page: response.data, isLoadingWaves: false});
            });
    }

    render() {
        if (this.state.isLoadingWaves){
            return (<Spinner/>);
        }
        if (this.state.page.content.length === 0){
            return (
                <div className = "card card-header text-center">
                    There are no waves
                </div>
            );
        } 
        return <div></div>
    }
}

export default WaveFeed;
